import React, { Component } from "react";
import axios from "axios";
import lstyles from '../localstyles/login.module.css'

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: ""
        }
    }

    updateForm = (event) => {
        let nam = event.target.name;
        let val = event.target.value;
        let data = this.state.logindata;
        data[nam] = val
        this.setState({ logindata: data });
    }

    render() {
        return (
            <section className={`hero is-fullheight ${lstyles.wavebg}`}>
                <div className="hero-body">
                    <div className="container has-text-centered">
                        <div className="column is-4 is-offset-4">
                            <div className="box">

                                <h3 className="title">Login</h3>
                                <figure className="avatar">
                                    <img width='64' alt="logo" src="https://external-content.duckduckgo.com/iu/?u=http%3A%2F%2Fwww.freepnglogos.com%2Fuploads%2Ftelegram-logo-15.png&f=1&nofb=1" />
                                </figure>
                                <br/>
                                <form>                 
                                    <div className="field">
                                        <div className="control">
                                            <input className="input is-normal" type="email" onChange={this.updateForm} name="user_email" placeholder="Your Email" autoFocus=""/>
                                        </div>
                                    </div>

                                    <div className="field">
                                        <div className="control">
                                            <input className="input is-normal" type="password" onChange={this.updateForm} name="user_password" placeholder="Your Password"/>
                                        </div>
                                    </div>

                                    <div className="field">
                                        <label className="checkbox">
                                        <input type="checkbox"/>
                                        &nbsp;Remember me
                                        </label>
                                    </div>
                                    <button className="button is-block is-info is-normal is-fullwidth">Login <i className="fa fa-sign-in" aria-hidden="true"></i></button>
                                </form>
                            </div>
                            <p className="has-text-white">
                                <a href="/signup">Sign Up</a> &nbsp;-&nbsp;&nbsp;
                                <a href="../">Forgot Password</a>
                            </p>
                        </div>
                    </div>
                </div>
            </section>
        )
    }
}

export default Login;
